export const logWithPlus = (text) => {
    console.log(' ++++++++++++++ ' + text + ' +++++++++++++++++++');
}

export const logWithMinus = (text) => {
    console.log(' -------------- ' + text + ' -------------------');
}

export const logConsole = (text) => {
    console.log(text);
}

export const logStart = (text) => {
    console.log('--- Start:   ' + text);
}

export const logContinue = (text) => {
    console.log('--- Continue:   ' + text);
}

export const logEnd = (text) => {
    console.log('--- End:   ' + text);
}

